--Query 4 update the inv-description text from 'small interiors' to 'a huge interior'.
UPDATE inventory
SET inv_description = REPLACE(inv_description, 'small interiors', 'a huge interior')
WHERE inv_make = 'GM' AND inv_model = 'Hummer';

SELECT *
FROM inventory;



--Query 5  Inner joing to view inv-make, inv_model and classification_name of inventory items 
--that belong to "Sport" category.
SELECT inv.inv_make AS make, inv.inv_model AS model, clas.classification_name AS classification
FROM inventory inv INNER JOIN classification clas
ON inv.classification_id = clas.classification_id
WHERE clas.classification_name = 'Sport';


