--Query 6 Changing path from '/images' to '/images/vehicles'
SELECT *
FROM inventory;

UPDATE inventory
SET inv_image = REPLACE(inv_image, '/images', '/images/vehicles'),
	inv_thumbnail = REPLACE(inv_thumbnail, '/images', '/images/vehicles');
	
SELECT *
FROM inventory;

